	public String maintainCatalogueOperations(
			MaintainCatalogueRequestType maintainCatalogueRequest)
			throws Exception {
		try {
			PreparedStatement vPreparedStatement = null;
			Connection vConnection = null;
			vConnection = getDBConnection();
			ResultSet vResultSet = null;
			if (maintainCatalogueRequest.getProductIdentifier() != null
					&& maintainCatalogueRequest.getOperationType()
							.equalsIgnoreCase("INSERT")) {
				PreparedStatement vPreparedStatement1 = vConnection
						.prepareStatement("INSERT INTO "
								+ maintainCatalogueRequest.getTableName()
								+ " (ID,JSONDETAILS,XMLDETAILS) values (?, ?, ?)");
				vPreparedStatement1.setString(1,
						maintainCatalogueRequest.getProductIdentifier());
				vPreparedStatement1.setString(2,
						maintainCatalogueRequest.getJsonData());
				vPreparedStatement1.setString(3,
						maintainCatalogueRequest.getXmlData());
				vPreparedStatement1.executeUpdate();
			} else if (maintainCatalogueRequest.getProductIdentifier() != null
					&& maintainCatalogueRequest.getOperationType()
							.equalsIgnoreCase("UPDATE")) {
				if (maintainCatalogueRequest.getJsonData() != null
						&& maintainCatalogueRequest.getXmlData() != null
						&& maintainCatalogueRequest.getJsonData().length() > 0
						&& maintainCatalogueRequest.getXmlData().length() > 0) {
					PreparedStatement vPreparedStatement1 = vConnection
							.prepareStatement("UPDATE "
									+ maintainCatalogueRequest.getTableName()
									+ " SET JSONDETAILS = ?, XMLDETAILS = ? WHERE ID = ?");
					vPreparedStatement1.setString(1,
							maintainCatalogueRequest.getJsonData());
					vPreparedStatement1.setString(2,
							maintainCatalogueRequest.getXmlData());
					vPreparedStatement1.setString(3,
							maintainCatalogueRequest.getProductIdentifier());
					vPreparedStatement1.executeUpdate();
				} else if (maintainCatalogueRequest.getJsonData() != null
						&& maintainCatalogueRequest.getJsonData().length() > 0
						&& maintainCatalogueRequest.getXmlData().length() <= 0) {
					PreparedStatement vPreparedStatement1 = vConnection
							.prepareStatement("UPDATE "
									+ maintainCatalogueRequest.getTableName()
									+ " SET JSONDETAILS = ? WHERE ID = ?");
					vPreparedStatement1.setString(1,
							maintainCatalogueRequest.getJsonData());
					vPreparedStatement1.setString(2,
							maintainCatalogueRequest.getProductIdentifier());
					vPreparedStatement1.executeUpdate();
				} else if (maintainCatalogueRequest.getXmlData() != null
						&& maintainCatalogueRequest.getXmlData().length() > 0
						&& maintainCatalogueRequest.getJsonData().length() <= 0) {
					PreparedStatement vPreparedStatement1 = vConnection
							.prepareStatement("UPDATE "
									+ maintainCatalogueRequest.getTableName()
									+ " SET XMLDETAILS = ? WHERE ID = ?");
					vPreparedStatement1.setString(1,
							maintainCatalogueRequest.getXmlData());
					vPreparedStatement1.setString(2,
							maintainCatalogueRequest.getProductIdentifier());
					vPreparedStatement1.executeUpdate();
				}
			} else if (maintainCatalogueRequest.getProductIdentifier() != null
					&& maintainCatalogueRequest.getOperationType()
							.equalsIgnoreCase("DELETE")) {
				vPreparedStatement = vConnection
						.prepareStatement("DELETE FROM "
								+ maintainCatalogueRequest.getTableName()
								+ " WHERE ID = '"
								+ maintainCatalogueRequest
										.getProductIdentifier() + "'");
				vPreparedStatement.execute();
			}

		} catch (Exception e) {
			mLog.error(e.getMessage());
			e.printStackTrace();
			throw e;
		}

		return "success";
	}

